mapper.select:
SELECT place.id, plan_id, attraction.id as attr_id, name, address, latitude, longitude, plan_date, ord
FROM place
LEFT JOIN attraction ON place.attr_id = attraction.id
WHERE plan_id = #{planId}
ORDER BY plan_date ASC, ord ASC;

mapper.update:
UPDATE place
SET plan_date = #{planDate}, ord = #{order}
WHERE id = #{id}
